// src/App.js

import React, { useState, useEffect, useMemo } from 'react';
import Papa from 'papaparse';
import { motion } from 'framer-motion';
import { Search } from 'lucide-react';

// If you want to use the Card, CardContent, etc., from @shadcn/ui, you can either
// directly import them or create stubs that use regular <div> elements for now.
import { Card, CardContent } from "@/components/ui/card"; 
import { Input } from "@/components/ui/input";

// Debounce hook
function useDebounce(value, delay = 300) {
  const [debouncedValue, setDebouncedValue] = useState(value);
  useEffect(() => {
    const handler = setTimeout(() => setDebouncedValue(value), delay);
    return () => clearTimeout(handler);
  }, [value, delay]);
  return debouncedValue;
}

function App() {
  const [csvData, setCsvData] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const debouncedSearch = useDebounce(searchTerm, 300);

  useEffect(() => {
    Papa.parse(
      "https://raw.githubusercontent.com/smjoud/NSpolidonations/main/data.csv",
      {
        download: true,
        header: true,
        skipEmptyLines: true,
        complete: (results) => {
          setCsvData(results.data);
        },
      }
    );
  }, []);

  // Color rows by party
  function getRowColor(partyValue = "") {
    const p = partyValue.toLowerCase();
    if (p.includes("pc")) return "#639ee0";
    if (p.includes("liberal")) return "red";
    if (p.includes("ndp")) return "orange";
    if (p.includes("green")) return "green";
    if (p.includes("atlantica")) return "purple";
    return "";
  }

  // Compute donation totals by party
  function getPartyTotals(data) {
    const totals = {
      pc: 0, ndp: 0, liberal: 0, cpc: 0, ndpca: 0, lpc: 0,
    };
    data.forEach((row) => {
      // Adjust column name to match your CSV
      const amountStr = row["Amount"] || row["Donation"] || "0";
      const amount = parseFloat(amountStr.replace(/[$,]/g, "")) || 0;
      const party = (row["Party"] || "").toLowerCase();

      if (party.includes("pc")) totals.pc += amount;
      if (party.includes("ndpca")) totals.ndpca += amount;
      if (party.includes("ndp")) totals.ndp += amount;
      if (party.includes("liberal")) totals.liberal += amount;
      if (party.includes("cpc")) totals.cpc += amount;
      if (party.includes("lpc")) totals.lpc += amount;
    });
    return totals;
  }

  // Filter data via useMemo to reduce lag
  const filteredData = useMemo(() => {
    if (!debouncedSearch) return [];
    const lowerSearch = debouncedSearch.toLowerCase();
    return csvData.filter((row) =>
      Object.values(row).join(" ").toLowerCase().includes(lowerSearch)
    );
  }, [csvData, debouncedSearch]);

  // Summaries for the current filtered data
  const partyTotals = useMemo(() => getPartyTotals(filteredData), [filteredData]);

  return (
    <div className="p-4 min-h-screen bg-gray-50 flex flex-col items-center gap-6">
      <motion.h1
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        className="text-3xl font-bold"
      >
        CSV Search App
      </motion.h1>

      <Card className="w-full max-w-2xl">
        <CardContent className="p-4 flex flex-col gap-4">
          <div className="relative w-full">
            <Input
              type="text"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              placeholder="Type to search..."
              className="pl-10"
            />
            <Search className="absolute left-2 top-2.5 text-gray-400" size={20} />
          </div>

          {debouncedSearch && (
            <div className="overflow-x-auto">
              <table className="min-w-full text-left border-collapse">
                <thead>
                  <tr className="border-b">
                    {filteredData.length > 0 &&
                      Object.keys(filteredData[0]).map((header) => (
                        <th key={header} className="p-2 font-medium">
                          {header}
                        </th>
                      ))}
                  </tr>
                </thead>
                <tbody>
                  {filteredData.map((row, index) => {
                    const partyValue = row["Party"] || "";
                    return (
                      <tr
                        key={index}
                        className="border-b hover:bg-gray-100"
                        style={{ backgroundColor: getRowColor(partyValue) }}
                      >
                        {Object.values(row).map((val, i) => (
                          <td key={i} className="p-2">
                            {val}
                          </td>
                        ))}
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>
          )}
        </CardContent>
      </Card>

      {debouncedSearch && (
        <div className="mt-6 w-full max-w-2xl bg-white rounded-md shadow p-4">
          <h2 className="text-lg font-bold mb-4">Total Amount Donated</h2>
          <div className="mb-4 flex items-center">
            <img
              src="https://raw.githubusercontent.com/smjoud/NSpolidonations/main/img/nspc_logo.jpg"
              alt="PC Party Logo"
              className="w-10 h-10 rounded-full mr-3"
            />
            <span className="font-semibold">PC:</span>
            <span className="ml-2">${partyTotals.pc.toFixed(2)}</span>
          </div>

          {/* Repeat this pattern for other parties */}
          {/* e.g. NDP, Liberal, CPC, NDPCA, LPC */}
        </div>
      )}

      <div className="text-sm text-gray-600 text-center mt-4">
        <p>This component automatically loads the data from the online CSV.</p>
        <p>Type a query in the search box above to filter and display matching rows.</p>
      </div>
    </div>
  );
}

export default App;
